'use strict';
module.exports.InvalidSignalError = class InvalidSignalError extends Error {

    constructor(message) {

        super(message);
        this.name = 'InvalidSignalError';
    }
};

module.exports.AuthenticationError = class AuthenticationError extends Error {

    constructor() {

        super('Access token is missing or invalid');
        this.name = 'AuthenticationError';
    }
};

module.exports.AuthenticationNotInitError = class AuthenticationNotInitError extends Error {

    constructor() {

        super('Cannot send data without an API key or a session key.');
        this.name = 'AuthenticationNotInitError';
    }
};

module.exports.UnknownError = class UnknownError extends Error {

    constructor(message) {

        super(message);
        this.name = 'UnknownError';
    }
};
