'use strict';
const Code = require('code');
const Lab = require('lab');
const lab = exports.lab = Lab.script();

const describe = lab.describe;
const it = lab.it;
const expect = Code.expect;

const IFace = require('../../../lib/ecosystem/ecosystemInterface');

describe('Ecosystem', () => {

    describe('Interface', () => {

        it('should create an interface', { plan: 1 }, (done) => {

            const curr = new IFace('hello', 'world');
            expect(curr).to.equal({ name: 'hello', logger: 'world' });
            done();
        });
    });
});
