'use strict';
const Code = require('code');
const Lab = require('lab');
const lab = exports.lab = Lab.script();
const beforeEach = lab.beforeEach;

const describe = lab.describe;
const it = lab.it;
const expect = Code.expect;

const Metric = require('../../../lib/metric');
const Sum = require('../../../lib/metric/sum');
const SqreenSDK = require('sqreen-sdk');

describe('Metric', () => {

    beforeEach((done) => {

        Metric._clearAllMetrics();
        require('../../../lib/command/features').featureHolder.use_signals = true;
        done();
    });

    describe('Sum', () => {

        it('should create a sum metric', { plan: 4, timeout: 20000 }, (done) => {

            SqreenSDK.initBatch(100000, 100, () => Promise.resolve());
            const sum = new Sum({
                kind: 'Sum',
                name: 'login-fail',
                period: 2
            });

            sum.add('key1', 1);
            sum.add('key2', 1);

            expect(Metric.getMetricByName('login-fail')).to.equal(sum);
            setTimeout(() => {

                sum.add('key1', 1);
                sum.add('key1', 1);
                sum.add('key3', 1);
            }, 3000);

            setTimeout(() => {

                Metric.getAllReports(true);
                const metricSignals = SqreenSDK.Signal.prototype.BATCH.data.filter((x) => x.signal_name === 'sq.agent.metric.login-fail');
                expect(metricSignals).to.have.length(2);
                expect(metricSignals[0].payload.values).to.equal([{ key: 'key1', value: 1 }, { key: 'key2', value: 1 }]);
                expect(metricSignals[1].payload.values).to.equal([{ key: 'key1', value: 2 }, { key: 'key3', value: 1 }]);
                done();
            }, 6000);

        });

        it('should create a sum metric and test plenty of things', { plan: 3, timeout: 20000 }, (done) => {

            const sum = new Sum({
                kind: 'Sum',
                name: 'test-sum',
                period: 5
            });

            sum.add('str', 1);
            sum.add(null, 1);
            sum.add({ a: { b: {} } }, 1);
            sum.add([{ a: { b: {} } }], 1);
            sum.add([{ a: { b: { c: { d: {} } } } }], 1);
            sum.add(10, 1);
            sum.add({ a: 1 }, 1);
            sum.add({ a: 1 }, 1);
            sum.add({ b: 1 }, 1);
            sum.add([1, 2, 'hello'], 1);
            sum.add([1, 'hello', 2], 1);
            sum.add(true, 1);

            setTimeout(() => {

                sum.process();
                Metric.getAllReports(true);
                const report = SqreenSDK.Signal.prototype.BATCH.data.find((x) => x.signal_name.endsWith('test-sum'));

                expect(report).to.exist();

                expect(report.payload.values).to.equal([
                    { key: 'str', value: 1 },
                    { key: { a: { b: {} } }, value: 1 },
                    { key: [{ a: { b: {} } }], value: 1 },
                    { key: { a: 1 }, value: 2 },
                    { key: { b: 1 }, value: 1 },
                    { key: [1, 2, 'hello'], value: 1 },
                    { key: [1, 'hello', 2], value: 1 },
                    { key: true, value: 1 }
                ]);

                expect(report.payload.date_started.getSeconds() % 5).to.equal(0);

                done();
            }, 5000);
        });

    });
});
