'use strict';
const Lab = require('lab');
const Code = require('code');
const lab = exports.lab = Lab.script();

const describe = lab.describe;
const it = lab.it;
const expect = Code.expect;

const SafeUtils = require('../../lib/safeUtils');
const Path = require('path');

describe('safeUtils.guessRoot', () => {

    it.skip('should fing the place where node_modules is', { plan: 1 }, (done) => {

        const cwd = process.cwd();
        const root = SafeUtils.guessRoot();
        expect(root).to.equal(Path.join(cwd, 'node_modules', 'lab'));
        done();
    });
});

