'use strict';
const Code = require('code');
const Lab = require('lab');
const lab = exports.lab = Lab.script();
const beforeEach = lab.beforeEach;

const describe = lab.describe;
const it = lab.it;
const expect = Code.expect;

const Metric = require('../../../lib_old/metric');
let Collect = require('../../../lib_old/metric/collect');

describe('Metric Old', () => {

    beforeEach((done) => {

        Metric._clearAllMetrics();
        Collect = require('../../../lib_old/metric/collect');
        require('../../../lib/command/features').featureHolder.use_signals = false;
        done();
    });

    describe('Collect', () => {

        it('should create a collect metric', { plan: 3, timeout: 20000 }, (done) => {

            const M0 = Object.getPrototypeOf(Collect);
            M0.getAllReports(true);
            const collect = new Collect({
                kind: 'Collect',
                name: 'login-fail',
                period: 2
            });

            collect.add('key1', 1);
            collect.add('key2', 1);
            collect.add(null, 1);
            collect.add({ a: 1 }, 1);
            collect.add({ a: 1 }, 'hello');

            expect(M0.getMetricByName('login-fail')).to.equal(collect);
            setTimeout(() => {

                collect.add('key1', 1);
                collect.add('key1', 1);
                collect.add('key3', 1);
            }, 3000);

            setTimeout(() => {

                collect.process();
                expect(collect.values.map((x) => x.observation)).to.equal([{ 'key1': [1], 'key2': [1] }, { 'key1': [1, 1], 'key3': [1] }]);
                expect(collect.values.map((x) => x.object_observation)).to.equal([[{ key: { a: 1 }, value: [1, 'hello'] }],[]]);
                done();
            }, 6000);
        });
    });
});
