'use strict';
const Assert = require('assert');
module.exports.validateStub = function (module, stub) {

    const keys = Object.keys(module);
    const stubKeys = Object.keys(stub);
    stubKeys.forEach((k) => {

        Assert(keys.indexOf(k) > -1, `Invalid stub: ${k} is not in ${keys.join(', ')}`);
    });
};
